CREATE TABLE "public"."nyay_panchayats" ("id" serial NOT NULL, "name" bpchar NOT NULL, "district_id" integer NOT NULL, "block_id" integer NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("district_id") REFERENCES "public"."districts"("id") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("block_id") REFERENCES "public"."blocks"("id") ON UPDATE cascade ON DELETE restrict, UNIQUE ("name"));
